trigger_prototype.expression:
{Template Cisco IOS Software releases 12.2_3.5_ or later SNMPv1:sensor.temp.value[ciscoEnvMonTemperatureValue.{#SNMPINDEX}].avg(5m)}>{$TEMP_WARN:"{#SNMPVALUE}"}
							or
							{Template Cisco IOS Software releases 12.2_3.5_ or later SNMPv1:sensor.temp.status[ciscoEnvMonTemperatureState.{#SNMPINDEX}].last(0)}={$TEMP_WARN_STATUS}

dependency.expression:
{Template Cisco IOS Software releases 12.2_3.5_ or later SNMPv1:sensor.temp.value[ciscoEnvMonTemperatureValue.{#SNMPINDEX}].avg(5m)}>{$TEMP_CRIT:"{#SNMPVALUE}"}
							or
							{Template Cisco IOS Software releases 12.2_3.5_ or later SNMPv1:sensor.temp.status[ciscoEnvMonTemperatureState.{#SNMPINDEX}].last(0)}={$TEMP_CRIT_STATUS}

								or
								{Template Cisco IOS Software releases 12.2_3.5_ or later SNMPv1:sensor.temp.status[ciscoEnvMonTemperatureState.{#SNMPINDEX}].last(0)}={$TEMP_DISASTER_STATUS}

