							or
							{Template Cisco IOS Software releases 12.2_3.5_ or later SNMPv1:sensor.temp.status[ciscoEnvMonTemperatureState.{#SNMPINDEX}].last(0)}={$TEMP_CRIT_STATUS}

								or
								{Template Cisco IOS Software releases 12.2_3.5_ or later SNMPv1:sensor.temp.status[ciscoEnvMonTemperatureState.{#SNMPINDEX}].last(0)}={$TEMP_DISASTER_STATUS}
